function ExportAPI_Data ($url,$Path )
{
$response = Invoke-RestMethod -Uri $url -Proxy http://proxy.gassco.no:4040/ 
$response.operationaldatas| Export-Csv -Path $Path -NoTypeInformation -Force -Encoding Default  
 
}


